//CSS
import './App.css';

// React
import {useCallback, useEffect, useState} from "react";

//Data
import {wordList} from "./data/word";


//Components
import StartScreen from './components/StartScreen';
import Game from "./components/Game";
import End from "./components/End";

const stages = [
  {id: 0, name: "start"},
  {id: 1, name: "game"},
  {id: 2, name: "end"},
]

const guessesQty = 3

function App() {

    const [gameStage, setGameStage] = useState(stages[0].name);
    const [word] = useState(wordList);

    const [pickedWord, setPickedWord] = useState ("");
    const [pickedCategory, setPickedCategory] = useState ("");
    const [letters, setLetters] = useState([]);

    const [guessedLetters, setGuessedLetters] = useState([])
    const [wrongLetters, setWrongLetters] = useState ([])
    const [guessed, setGuessed] = useState (guessesQty)
    const [score, setScore] = useState (0)



    const pickWordAndCategory = useCallback(() => {
      //pick a random category
      const categories = Object.keys(word);
      const category = 
        categories[Math.floor(Math.random() * categories.length)]

      //pick a random word
      const words =
         word[category][Math.floor(Math.random() * word[category].length)];


      return {category, words}
    }, [word]);

    //start game
    const startGame = useCallback(() => {
      //clear all letters
      clearLetterStates();

      // choose a word
      const { category, word } = pickWordAndCategory();

      // create an array of letters
      let wordLetters = word.split("");

      wordLetters = wordLetters.map((l) => l.toLowerCase());

      //fill states
      setPickedWord(word)
      setPickedCategory(category)
      setLetters(wordLetters)

      setGameStage(stages[1].name);
    },[pickWordAndCategory])


      // pick word and pick category
      //const {word, category } = pickWordAndCategory();


    //process the letter input
    const verifyLetter = (letter) => {
      const normalizedLetter = letter.toLowerCase();

      //check if letter has already been utilized
      if(guessedLetters.includes(normalizedLetter) || wrongLetters.includes(normalizedLetter) ){
      return;
      }

      //push guessed letter or remove a guess 
      if (letters.includes(normalizedLetter)){ 
        setGuessedLetters((actualGuessedLetters) => [...actualGuessedLetters, letter,]);
      } else {
        setWrongLetters((actualWrongLetters) => [...actualWrongLetters, normalizedLetter])
      }

      setGuessed((actualGuessed) => actualGuessed - 1);

    };


//restarts the game
const retry = () => {

  setScore(0)
  setGuessed(guessesQty)
  setGameStage(stages[0].name);
}


const clearLetterStates = () => {
  setGuessedLetters([]);
  setWrongLetters([]);
};


useEffect(() => {
if(guessed === 0) {
//reset all states
clearLetterStates()

setGameStage(stages[2].name)
}
}, [guessed])

//check win condition
useEffect(() => {
const uniqueLetters = [...new Set(letters)];

//win condition
if(guessedLetters.length === uniqueLetters.length)
//add score
setScore((actualScore) => actualScore += 100)
//restart  game with new word
startGame();

}, [guessedLetters, letters, startGame]) ;

  return (
    <div className="App">
   {gameStage === "start" &&  < StartScreen startGame={startGame}/>}
   {gameStage === "game" && ( 
    < Game 
        verifyLetter={verifyLetter}
        pickedWord={pickedWord}  
        pickedCategory={pickedCategory} 
        letters={letters} 
        guessedLetters={guessedLetters} 
        wrongLetters={wrongLetters} 
        guessed={guessed} 
        score={score}
        />)}

   {gameStage === "end" &&  < End retry={retry} score={score}/>}
    </div>
  );
}

export default App;
